import { useState } from 'react';
import axios from 'axios';

const ReviewForm = ({ recipeId }) => {
  const [review, setReview] = useState('');
  const [rating, setRating] = useState(1);
  const [error, setError] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!recipeId || !review.trim() || !rating) {
      setError('All fields are required.');
      return;
    }

    try {
      const response = await axios.post(
        'http://localhost:5000/api/reviews/submit',
        { recipeId, review, rating },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'application/json',
          },
        }
      );
      console.log('Review submitted successfully:', response.data);
      // Handle successful review submission (e.g., show a success message or redirect)
    } catch (error) {
      console.error('Review submission error:', error.response ? error.response.data : error.message);
      setError('Error submitting review. Please check your input and try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Submit Your Review</h2>
      <div>
        <label htmlFor="rating">Rating:</label>
        <select
          id="rating"
          value={rating}
          onChange={(e) => setRating(parseInt(e.target.value, 10))}
        >
          {[1, 2, 3, 4, 5].map((num) => (
            <option key={num} value={num}>{num}</option>
          ))}
        </select>
      </div>
      <div>
        <label htmlFor="review">Review:</label>
        <textarea
          id="review"
          value={review}
          onChange={(e) => setReview(e.target.value)}
          required
        />
      </div>
      <button type="submit">Submit Review</button>
      {error && <div className="error">{error}</div>}
    </form>
  );
};

export default ReviewForm;
