import { useState, useEffect } from 'react';
import axios from 'axios';

const FavoriteRecipes = () => {
  const [favorites, setFavorites] = useState([]);
  const [error, setError] = useState(null); // To handle errors

  useEffect(() => {
    const fetchFavorites = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/favorites/', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setFavorites(response.data);
      } catch (error) {
        console.error('Error fetching favorite recipes:', error);
        setError('Error fetching favorite recipes'); // Set the error state
      }
    };

    fetchFavorites();
  }, []);

  if (error) {
    return <div>{error}</div>; // Display error message if there is an error
  }

  return (
    <div>
      <h2>Your Favorite Recipes</h2>
      {favorites.length === 0 ? (
        <p>No favorite recipes found.</p>
      ) : (
        <ul>
          {favorites.map((fav) => (
            fav.recipe ? ( // Check if fav.recipe is defined
              <li key={fav.id}>
                <h3>{fav.recipe.title}</h3>
                <p>{fav.recipe.ingredients}</p>
              </li>
            ) : (
              <li key={fav.id}>Recipe data unavailable</li>
            )
          ))}
        </ul>
      )}
    </div>
  );
};

export default FavoriteRecipes;
