import { useState, useEffect } from "react";
import FavoriteButton from "../FavoriteButton";
import { useParams } from "react-router-dom";
import axios from "axios";
const RecipeDetails = () => {
  const [recipes, setRecipes] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    axios.get(
      "https://localhost:5000/api/recipes/${id"
        .then((response) => setRecipes(response.data))
        .catch((error) => console.error("error fetching details:", error))
    );
  },[id]);

  if(!recipes)
  {
     return <p>Loading.....</p>
  }
  return (
    <div>
      <h1>{recipes.title}</h1>
      <img src={recipes.imageURL} alt={recipes.title} />
      <p><strong>Ingredients:</strong> {recipes.ingredients}</p>
      <p><strong>Instruction:</strong> {recipes.instruction}</p>
      <p><strong>Cooking Time:</strong> {recipes.cookingTime}</p>
      <p><strong>Serving Time:</strong> {recipes.servingTime}</p>
      <p><strong>Dietary Preference:</strong> {recipes.dietaryPreference}</p>
      <p><strong>Difficulty:</strong> {recipes.difficulty}</p>
      <FavoriteButton recipeId={id} />
    </div>
  );


};
export default RecipeDetails;
