import { useState } from 'react';
import axios from 'axios';

const ReviewForm = ({ recipeId }) => {
  const [review, setReview] = useState('');
  const [rating, setRating] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors
  
    // Check if all fields are filled and if rating is a valid number
    if (!recipeId || !review || !rating || isNaN(Number(rating))) {
      setError('All fields are required, and rating must be a valid number.');
      return;
    }
  
    try {
      const response = await axios.post(
        'http://localhost:5000/api/reviews/submit',
        { recipeId, review, rating: Number(rating) }, // Convert rating to number
        { headers: { 'Content-Type': 'application/json' } }
      );
      console.log('Review submitted:', response.data);
    } catch (error) {
      console.error('Review submission error:', error);
      setError('Error submitting review');
    }
  };
  

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="review">Review:</label>
        <textarea
          id="review"
          value={review}
          onChange={(e) => setReview(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="rating">Rating:</label>
        <input
          type="number"
          id="rating"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
          required
        />
      </div>
      <button type="submit">Submit Review</button>
      {error && <p>{error}</p>}
    </form>
  );
};

export default ReviewForm;
