import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";


const RecipeList = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/recipes/search")
      .then((response) => {
        const data = response.data;
        if (Array.isArray(data.recipes)) {
          setRecipes(data.recipes);
        } else {
          console.error("Unexpected data format, expected array");
        }
      })
      .catch((error) => console.error("Error fetching recipes:", error));
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/recipes/delete/${id}`); // Fix URL template
      setRecipes(recipes.filter((recipe) => recipe.id !== id)); // Filter out the deleted recipe
    } catch (error) {
      console.error("Error deleting recipe:", error);
    }
  };

  return (
    <div>
      <h1>Recipe List</h1>
      <Link to="/recipes/create">Create New Recipe</Link>
      <ul>
        {recipes.length > 0 ? (
          recipes.map((recipe) => (
            <li key={recipe.id}>
              <h2>{recipe.title}</h2>
              <Link to={`/recipes/${recipe.id}`}>View Details</Link>
              <Link to={`/recipes/edit/${recipe.id}`}>Edit</Link>
              <button onClick={() => handleDelete(recipe.id)}>Delete</button>
            </li>
          ))
        ) : (
          <p>No recipes available</p>
        )}
      </ul>
    </div>
  );
};

export default RecipeList;
