import { useState, useEffect } from 'react';
import axios from 'axios';

const ActivityFeed = () => {
  const [activities, setActivities] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5000/api/follow/feed', {
      headers: {
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    })
      .then(response => {
        setActivities(response.data);
        setLoading(false);
      })
      .catch(error => console.error('Error fetching activity feed:', error));
  }, []);

  if (loading) return <p>Loading activity feed...</p>;

  return (
    <div>
      <h2>Activity Feed</h2>
      <ul>
        {activities.map(activity => (
          <li key={activity.id}>
            <p><strong>{activity.user.name}</strong> {activity.action}: {activity.recipe.title}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ActivityFeed;
