import { useState, useEffect } from "react";
import FavoriteButton from "../FavoriteButton";
import { useParams } from "react-router-dom";
import axios from "axios";

const RecipeDetails = () => {
  const [recipe, setRecipe] = useState(null); // Use singular 'recipe' to match the single item being fetched
  const { id } = useParams();

  useEffect(() => {
    axios.get(`https://localhost:5000/api/recipes/${id}`)
      .then((response) => setRecipe(response.data))
      .catch((error) => console.error("Error fetching details:", error));
  }, [id]);

  if (!recipe) {
    return <p>Loading.....</p>;
  }

  return (
    <div>
      <h1>{recipe.title}</h1>
      <img src={recipe.imageURL} alt={recipe.title} />
      <p><strong>Ingredients:</strong> {recipe.ingredients}</p>
      <p><strong>Instructions:</strong> {recipe.instruction}</p>
      <p><strong>Cooking Time:</strong> {recipe.cookingTime}</p>
      <p><strong>Serving Time:</strong> {recipe.servingTime}</p>
      <p><strong>Dietary Preference:</strong> {recipe.dietaryPreference}</p>
      <p><strong>Difficulty:</strong> {recipe.difficulty}</p>
      <FavoriteButton recipeId={id} />
    </div>
  );
};

export default RecipeDetails;
