import { useState } from 'react';
import axios from 'axios';

const ReviewForm = ({ recipeId }) => {
  const [review, setReview] = useState('');
  const [rating, setRating] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Ensure recipeId exists, review is not empty, and rating is a valid number
    if (!recipeId || review.trim() === '' || isNaN(Number(rating)) || Number(rating) < 1 || Number(rating) > 5) {
      setError('All fields are required, and rating must be a valid number between 1 and 5.');
      setSuccess('');
      return;
    }

    try {
      const response = await axios.post(
        'http://localhost:5000/api/reviews/submit',
        { 
          recipeId, 
          review, 
          rating: Number(rating)  // Ensure rating is a number
        },
        { headers: { 'Content-Type': 'application/json' } }
      );
      console.log('Review submitted:', response.data);
      setError('');
      setSuccess('Review submitted successfully!');
    } catch (error) {
      console.error('Review submission error:', error);
      setError('Error submitting review. Please try again.');
      setSuccess('');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="review">Review:</label>
        <textarea
          id="review"
          value={review}
          onChange={(e) => setReview(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="rating">Rating (1-5):</label>
        <input
          type="number"
          id="rating"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
          min="1"
          max="5"
          step="1"
          required
        />
      </div>
      <button type="submit">Submit Review</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
    </form>
  );
};

export default ReviewForm;
